// ==UserScript==
// @name         Auto Template Color
// @namespace    http://tampermonkey.net/
// @version      0.2
// @description  Allows you to place colors that will auto-match to template. This is legal for pxls since you still have to manually place each pixel.
// @author       WindySpoon
// @match        https://pxls.space/
// @icon         data:image/gif;base64,
// @grant        none
// ==/UserScript==

// credits go to Temriel and Laylah for helping me test the script, and Kris for some borrowed logic

(function() {
    'use strict';

    function calcOffsets(realX, realY, scale, realWidth) {
        const templateBlockStartX = Math.floor(realX * scale);
        const templateBlockEndX = Math.floor(templateBlockStartX + scale);
        const templateBlockStartY = Math.floor(realY * scale);
        const templateBlockEndY = Math.floor(templateBlockStartY + scale);
        const realDataOffset = (realWidth * realY + realX) * 4;
        return { templateBlockStartX, templateBlockEndX, templateBlockStartY, templateBlockEndY, realDataOffset };
    }

    function findBlock(templateBlockStartX, templateBlockEndX, templateBlockStartY, templateBlockEndY, templateData, realDataOffset, detemplatizedData, image) {
        let found = false;
        for (let x = templateBlockStartX; x < templateBlockEndX; x++) {
            for (let y = templateBlockStartY; y < templateBlockEndY; y++) {
                const blockOffset = (y * image.width + x) * 4;

                if (templateData[blockOffset + 3] !== 0) {
                    detemplatizedData[realDataOffset] = templateData[blockOffset];
                    detemplatizedData[realDataOffset + 1] = templateData[blockOffset + 1];
                    detemplatizedData[realDataOffset + 2] = templateData[blockOffset + 2];
                    detemplatizedData[realDataOffset + 3] = 255;
                    found = true;
                    break;
                }
            }
            if (found) {
                break;
            }
        }
        return found;
    }

    function detemplatizeImage(image, scale, realWidth, realHeight) {
        const templateCanvas = document.createElement("canvas");
        const templateCtx = templateCanvas.getContext("2d");
        templateCanvas.width = image.width;
        templateCanvas.height = image.height;
        templateCtx.drawImage(image, 0, 0);
        const templateData = templateCtx.getImageData(0, 0, templateCanvas.width, templateCanvas.height).data;
        if (image.width !== realWidth) {
            const detemplatizedData = new Uint8Array(realWidth * realHeight * 4);
            for (let realY = 0; realY < realHeight; realY++) {
                for (let realX = 0; realX < realWidth; realX++) {
                    const { templateBlockStartX, templateBlockEndX, templateBlockStartY, templateBlockEndY, realDataOffset } = calcOffsets(realX, realY, scale, realWidth);
                    findBlock(templateBlockStartX, templateBlockEndX, templateBlockStartY, templateBlockEndY, templateData, realDataOffset, detemplatizedData, image);
                }
            }
            return (detemplatizedData);
        } else {
            return (templateData);
        }
    }

    function getColorAtPosition(data, width, x, y) {
        const pixelOffset = (y * width + x) * 4;
        const r = data[pixelOffset];
        const g = data[pixelOffset + 1];
        const b = data[pixelOffset + 2];
        const a = data[pixelOffset + 3];
        return [r, g, b, a];
    }

    function hexToRgba(hexValue) {
        const r = parseInt(hexValue.substring(0, 2), 16);
        const g = parseInt(hexValue.substring(2, 4), 16);
        const b = parseInt(hexValue.substring(4, 6), 16);
        return [r, g, b];
    }

    function cursorCoords() {
       const [x, y] = (document.querySelectorAll('#coords-info > .coords')[0].textContent.match(/\d+/g) || [0, 0]).map(Number);
       return {'x': x, 'y': y}
    }

    function getInfo() {
        return new Promise(async (resolve, reject) => {
            try {
                let response = await fetch(window.location.origin + "/info");
                let data = await response.json();
                resolve(data);
            } catch (error) {
                reject(error);
            }
        });
    }

    function getTemplateImage(templateObject) {
        return new Promise((resolve, reject) => {
            fetch(templateObject.template)
                .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.blob();
            })
                .then(createImageBitmap)
                .then(imageBitmap => {
                const realWidth = templateObject.tw;
                const scale = imageBitmap.width / realWidth;
                const realHeight = imageBitmap.height / scale;
                const result = detemplatizeImage(imageBitmap, scale, realWidth, realHeight);
                templateObject.th = realHeight;
                templateObject.template = result;
                resolve();
            })
                .catch(error => {
                reject(error);
            });
        });
    }

    function initiate(templateObject) {
        Promise.all([getInfo(), getTemplateImage(templateObject)])
            .then(([info]) => {

            const rgbPalette = info.palette.map(function(color) {
                return hexToRgba(color.value);
            });

            const board = document.getElementById('board')

            board.addEventListener('pointerdown', () => {
                const coords = cursorCoords();
                const translatedX = coords.x - templateObject.ox;
                const translatedY = coords.y - templateObject.oy;
                const color = getColorAtPosition(templateObject.template, templateObject.tw, translatedX, translatedY);

                let index = -1;

                if (color[3] !== 0 && translatedX >= 0 && translatedX < templateObject.tw &&
                    translatedY >= 0 && translatedY < templateObject.th) {
                    index = rgbPalette.findIndex(paletteColor => {
                        return paletteColor[0] === color[0] && paletteColor[1] === color[1] && paletteColor[2] === color[2];
                    });
                }
                const paletteButton = document.querySelector(`[data-idx="${index}"]`);
                paletteButton.click();
            });
        })
            .catch(error => {
            console.error('Error getting info or template data:', error);
        });
    }

    let waitForLoad = setInterval(() => {
        if(App) {
            clearInterval(waitForLoad)
            const templateObject = {
                template: "https://pxls.space/cors/" + document.getElementById('template-url').value,
                ox: parseInt(document.getElementById('template-coords-x').value),
                oy: parseInt(document.getElementById('template-coords-y').value),
                tw: parseInt(document.getElementById('template-width').value)
            };
            if (templateObject.tw) {
                initiate(templateObject);
            } else {
                console.warn('No template found');
            }
        }
    }, 1000)

})();